mapper.select:
SELECT id, department_id, email, name, role, password_hash, created_at, active
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users
SET name = #{name},
    email = #{email},
    role = #{role},
    department_id = #{department_id}
WHERE id = #{id}

UPDATE users
SET active = false
WHERE id = #{id}
